import React from "react";
import "../Style/App.css";
import AnimeCard from "./AnimeCard";
import "./Row";

function MainContent(props, { animeListaNovidades }) {
  return (
    <main>
      <div className="main-header" onSubmit={props.HandleSearch}>
        <form className="search-box">
          <input
            type="search"
            placeholder="Procure um anime aqui."
            required
            value={props.search}
            onChange={(e) => props.SetSearch(e.target.value)}
          />
        </form>
      </div>
      <h2>Destaques</h2>
      <div className="row">
        <div className="item">
          {props.animeLista.map((anime) => (
            <AnimeCard anime={anime} key={anime.mal_id} />
          ))}
        </div>
      </div>

      <h2>Novidades</h2>
      <div className="container">
        <div className="item">
          {props.animeListaNovidades.map((anime) => (
            <a href={anime.url} target="_blank" rel="noreferrer">
              <figure>
                <img src={anime.image_url} alt="" />
              </figure>
              <h3>{anime.title}</h3>
            </a>
          ))}
        </div>
      </div>

      <script src="MainContentCheck.js"></script>
    </main>
  );
}

export default MainContent;
