export default class Row extends React.Component(props, { title }) {
  constructor() {
    super();
    this.scroll = this.scroll.bind(this);
  }

  scroll(direction) {
    let far = ($(".image-container").width() / 2) * direction;
    let pos = $(".image-container").scrollLeft() + far;
    $(".image-container").animate({ scrollLeft: pos }, 1000);
  }

  render() {
    return (
      <div className="main">
        <h1 className="title">Scrolling Image Slider</h1>
        <div className="wrapper">
          <a className="prev" onClick={this.scroll.bind(null, -1)}>
            &#10094;
          </a>
          <div className="image-container">
            {animeListaNovidades.map((anime) => (
              <a
                className="row_poster"
                href={anime.url}
                target="_blank"
                rel="noreferrer"
              >
                <figure>
                  <img
                    key={anime.rank}
                    src={anime.image_url}
                    alt={anime.title}
                  />
                </figure>
              </a>
            ))}
          </div>
          <a className="next" onClick={this.scroll.bind(null, 1)}>
            &#10095;
          </a>
        </div>
      </div>
    );
  }
}
