import {Component} from 'react';
import '../Style/App.css';

class Home extends Component{

  state={
    animes:[]
  };
  componenteDidMount(){
    fetch('https://raw.githubusercontent.com/manami-project/anime-offline-database/master/anime-offline-database.json').then(response => response.json().then(animes =>this.setState({animes})))
  }

  render(){
    const {animes} = this.state;
    return (
     <div className="Anime">
       {
         animes.map(anime =>(
           <div key={anime.id}>
             <img src="{data.picture}" alt="{anime.data.title}" />
             <div>{anime.data.title}</div>
           </div>
         ))
       }

     </div>
    );
  
  }
  }

export default Home;
